import{_ as a,c as t,o,ae as l}from"./chunks/framework.BDcS7TL5.js";const r="/linux-notes/assets/20250308123342.oLXzruXc.png",_=JSON.parse('{"title":"系统安装","description":"","frontmatter":{},"headers":[],"relativePath":"notes/system-installation.md","filePath":"notes/system-installation.md"}'),n={name:"notes/system-installation.md"};function i(d,e,s,c,u,h){return o(),t("div",null,[...e[0]||(e[0]=[l('<h1 id="系统安装" tabindex="-1">系统安装 <a class="header-anchor" href="#系统安装" aria-label="Permalink to &quot;系统安装&quot;">​</a></h1><h2 id="版本经历" tabindex="-1">版本经历 <a class="header-anchor" href="#版本经历" aria-label="Permalink to &quot;版本经历&quot;">​</a></h2><ul><li>Deepin 23.1：<a href="https://github.com/duanluan/linux-notes/tree/deepin23" target="_blank" rel="noreferrer">GitHub - duanluan/deepin-notes at deepin23</a></li><li>Xubuntu 24.04.2：<a href="https://github.com/duanluan/linux-notes/tree/xubuntu24" target="_blank" rel="noreferrer">GitHub - duanluan/linux-notes at xubuntu24</a></li><li>Manjaro：当前版本</li></ul><h2 id="制作启动盘" tabindex="-1">制作启动盘 <a class="header-anchor" href="#制作启动盘" aria-label="Permalink to &quot;制作启动盘&quot;">​</a></h2><p>U 盘安装 <a href="https://www.ventoy.net/cn/index.html" target="_blank" rel="noreferrer">Ventoy</a>。</p><p><img src="'+r+'" alt="Ventoy2Disk"></p><p><a href="https://manjaro.org/" target="_blank" rel="noreferrer">Manjaro Homepage</a> 点击<code>Download</code>按钮，选择<code>KDE Plasma</code>版本下的<code>Download</code>下载 ISO 放入 U 盘。</p><h2 id="安装系统" tabindex="-1">安装系统 <a class="header-anchor" href="#安装系统" aria-label="Permalink to &quot;安装系统&quot;">​</a></h2><p>更多参考：<a href="https://wiki.manjaro.org/index.php/UEFI_-_Install_Guide/zh-cn" target="_blank" rel="noreferrer">UEFI - 安装指南 - Manjaro</a></p><p>开机按 <code>F2</code>/<code>Del</code> 进入 BIOS，<code>Save &amp; Exit</code> - <code>Boot Override</code> 选择 U 盘。</p><p>Ventoy 界面选择 Manjaro 镜像 - <code>Boot in normal mode</code>，如遇启动问题，尝试<code>Boot in grub2 mode</code>。</p><p>设置 tz（时区）为<code>Asia/Shanghai</code>、lang（语言）为<code>zh_CN</code>，设置后 pacman 会换源。</p><p>选择<code>Boot with open source drivers</code>，进入桌面后在弹出的<code>Manjaro Hello</code>窗口中点击<code>Launch installer</code>按钮。</p><p>安装程序设置：</p><ul><li>位置（Location） <ul><li>设置时区为 Asia/Shanghai。</li><li>系统语言设置为 简体中文（中国）。</li><li>数字和日期地域设置为 简体中文（中国）。</li></ul></li><li>键盘（Keyboard） <ul><li>键盘型号设置为 Generic 105-key PC。</li><li>键盘布局设置为 Chinese/Default。</li></ul></li><li>分区（Partitions） <ul><li>抹除磁盘</li><li>交换分区(无休眠) + ext4</li></ul></li><li>Office 套件（Office Suite） <ul><li>No Office Suite</li></ul></li></ul><p>安装完成后，重启前拔掉 U 盘。</p><h2 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-label="Permalink to &quot;参考&quot;">​</a></h2><ul><li><a href="https://blog.zhjh.top/?p=pe" target="_blank" rel="noreferrer">无广告纯净 PE 启动盘多系统收录</a></li><li><a href="https://ventoy.net/cn/doc_grub2boot.html" target="_blank" rel="noreferrer">grub2boot . Ventoy</a></li></ul>',18)])])}const f=a(n,[["render",i]]);export{_ as __pageData,f as default};
